document.addEventListener("DOMContentLoaded",()=>{let e=document.querySelector("header");window.addEventListener("scroll",()=>{50<window.scrollY?e.classList.add("sticky"):e.classList.remove("sticky")}),document.querySelectorAll("nav a").forEach(e=>{e.addEventListener("click",function(e){e.preventDefault();e=this.getAttribute("href").slice(1),e=document.getElementById(e);e&&e.scrollIntoView({behavior:"smooth"})})}),document.querySelectorAll(".character").forEach(e=>{e.addEventListener("mouseenter",()=>{e.style.transform="scale(1.1)",e.style.transition="transform 0.3s"}),e.addEventListener("mouseleave",()=>{e.style.transform="scale(1)"})}),document.querySelector("footer p").addEventListener("click",()=>{alert("Obrigado por visitar a p√°gina do Shrek!")})});